import React from 'react';
import { useState, useEffect } from 'react';
import HomedataJobs from '../HomePage//HomedataJobs';
import Container from '@material-ui/core/Container';
import axios from 'axios';

const LandingJobsCard = () => {
    const [data, setData] = useState([]);

    const getData = () => {
        console.log("getting data");
        axios.get('/uploadjob').then((response) => {
            const gettingdata = response.data;
            console.log(gettingdata);
            setData(() => {
                return (gettingdata)
            })
            console.log(data);
        })
    }

    useEffect(() => {
        getData()
    }, []);

    return (
        <>
            <Container className="home-card-container" >
                {
                    data.slice(0, 3).map((val, index) => {
                        return (
                            <>
                                <HomedataJobs
                                    title={val.title}
                                    description={val.description}
                                    comp_name={val.comp_name}
                                    domain={val.domain}
                                    req_skills={val.req_skills}
                                    add_skills={val.add_skills}
                                    location={val.location}
                                    education={val.education}
                                    experience={val.experience}
                                    ctc={val.ctc}
                                    link={val.link}
                                />
                            </>
                        );
                    })

                }
            </Container>
        </>
    )
}

export default LandingJobsCard;
